import React from 'react';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import { history, store} from '../../helpers';
import { userActions,locationAction } from '../../actions';
import $ from 'jquery'

class DefaultHeader extends React.Component {

  constructor(props){
    super(props);
    this.state = {
      latitude: '',
      longitude: '',
      fullAddress: ''
  }
    this.logoutUser = this.logoutUser.bind(this);
    this.getLoc = this.getLoc.bind(this)
  }

  logoutUser(){
    store.dispatch(userActions.logout());
    history.push('/');
  }

  getLoc() {
    store.dispatch(locationAction.getLocation());
    // console.log(this.state)
  }


  render() {
    const {fullAddress} = this.state      
    $(window).scroll(function() {
      // 100 = The point you would like to fade the nav in.

      if ($(window).scrollTop() > 70) {

          $('header').addClass('is-sticky');

      } else {

          $('header').removeClass('is-sticky');

      };
  });
    return (
      <header>
              <div class="nav-menu">
                    <div class="bg transition">
                        <div class="container-fluid">
                            <div class="row">
                                <div class="col-md-12">
                                    <nav class="navbar navbar-expand-lg navbar-light">
                                        <Link class="navbar-brand" to="/">Dreamers</Link>
                                        <button class="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarNavDropdown" aria-controls="navbarNavDropdown" aria-expanded="false" aria-label="Toggle navigation">
                                            <span class="icon-menu"></span>
                                        </button>
                                        <div class="collapse navbar-collapse justify-content-end" id="navbarNavDropdown">
                                            <ul class="navbar-nav">
                                               { !isLoggedIn() && <li><Link class="nav-link" to="/login"> Login  </Link> </li> }
                                                { !isLoggedIn() && <li><Link class="nav-link" to="/register"> Register </Link></li> }
                                                { isLoggedIn() && <li><Link class="nav-link" to="/Beproducer"> Become Producer </Link></li> }
                                                { isLoggedIn() && <li><Link class="nav-link" to="/profile"> Profile </Link></li> }
                                                { isLoggedIn() && <li><Link class="nav-link" onClick={this.logoutUser} to="/">Logout</Link></li> }
                                                <li><div class="btn btn-outline-light top-btn" onClick={this.getLoc}>{this.props.fullAddress ? this.props.fullAddress : "Know Your Location"}</div></li>
                                            </ul>
                                        </div>
                                    </nav>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
      </header>
    );
  }

}

function mapStateToProps(state) {
  return {
      fullAddress: state.Location.fullAddress,
  }
}

export default connect(mapStateToProps)(DefaultHeader);
